import R from 'ramda';
import { 
  FETCH_ACTIVITY_SUCCESS 
} from 'actionTypes';

const initialState = {};
export default (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_ACTIVITY_SUCCESS:
      const newVal = R.indexBy(R.prop('nodeid'), payload);
      // console.log('new val',newVal)
      return R.merge(state, newVal);
    default:
      return state;
  }
};
