import R from 'ramda';
import { FETCH_ACTIVITY_SUCCESS } from 'actionTypes';

const initialState = {
  ids: []
};
export default (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_ACTIVITY_SUCCESS:
      return R.merge(state, {
        ids: R.pluck('nodeid', payload)
      });
    default:
      return state;
  }
};
